import React, { useState } from 'react';
import { Container, Box, Drawer, List, ListItem, ListItemText, Typography, Divider, Button } from '@mui/material';
import { useNavigate } from 'react-router-dom';

const drawerWidth = 240;

function Dashboard() {
  const [selectedSection, setSelectedSection] = useState('Courses');
  const navigate = useNavigate();

  const handleSectionChange = (section) => {
    setSelectedSection(section);
  };

  const handleLogout = () => {
    // Implement logout logic here
    navigate('/login'); // Redirect to login page or wherever necessary
  };

  return (
    <Box sx={{ display: 'flex' }}>
      <Drawer
        sx={{
          width: drawerWidth,
          flexShrink: 0,
          '& .MuiDrawer-paper': {
            width: drawerWidth,
            boxSizing: 'border-box',
          },
        }}
        variant="permanent"
        anchor="left"
      >
        <List>
          <ListItem button onClick={() => handleSectionChange('Courses')}>
            <ListItemText primary="Courses" />
          </ListItem>
          <ListItem button onClick={() => handleSectionChange('Assignments')}>
            <ListItemText primary="Assignments" />
          </ListItem>
          <ListItem button onClick={() => handleSectionChange('Other')}>
            <ListItemText primary="Other" />
          </ListItem>
          <Divider />
          <ListItem button onClick={handleLogout}>
            <ListItemText primary="Logout" />
          </ListItem>
        </List>
      </Drawer>
      <Box
        component="main"
        sx={{ flexGrow: 1, bgcolor: 'background.default', p: 3 }}
      >
        <Typography variant="h4" gutterBottom>
          {selectedSection}
        </Typography>
        {selectedSection === 'Courses' && (
          <Box>
            <Button variant="contained" color="primary" onClick={() => navigate('/add-course')}>
              Add Course
            </Button>
            <Button variant="contained" color="primary" onClick={() => navigate('/view-courses')}>
              View Courses
            </Button>
            <Button variant="contained" color="primary" onClick={() => navigate('/update-course')}>
              Update Course
            </Button>
            <Button variant="contained" color="primary" onClick={() => navigate('/delete-course')}>
              Delete Course
            </Button>
          </Box>
        )}
        {selectedSection === 'Assignments' && (
          <Typography variant="h6">Assignments Section</Typography>
        )}
        {selectedSection === 'Other' && (
          <Typography variant="h6">Other Section</Typography>
        )}
      </Box>
    </Box>
  );
}

export default Dashboard;
