import React from "react";
import { useNavigate } from "react-router-dom";
import "./student.css";


function TeacherLogin() {
  const navigate = useNavigate();

  const handleLogin = (e) => {
    e.preventDefault(); // Prevent the default form submission

    // You can add your authentication logic here if needed

    // After successful login, navigate to the StudentDashboard page
    navigate('/teacherinterface');
  };

  return (
    <div className='container'>
      <div className='box form-box'>
        <header>LogIn</header>
        <form onSubmit={handleLogin}>
          <div className="field input">
            <input type="text" name="username" id="username" required placeholder="Enter username" />
            <i className='bx bxs-user'></i>
          </div>
          <div className="field input">
            <input type="password" name="password" id="password" required placeholder="Enter password" />
            <i className='bx bxs-lock-alt'></i>
          </div>
          <div className="field input">
            <input type="submit" className="btn" name="submit" value="Login" />
          </div>
          <div className="link">
            Don't have an account? <a href="register.html">Sign Up Now</a>
          </div>
        </form>
      </div>
    </div>
  );

}

export default TeacherLogin;
