import React, { useState } from 'react';
import { Container, Paper, Typography, TextField, Button, Link, Box } from '@mui/material';
import './student.css';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function StudentLogin() {
  const navigate = useNavigate();
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post("http://localhost:8070/student/login", {
        name: username,
        password: password
      });

      console.log('Response:', response.data); // Debugging: Log the response

      if (response.data.status === "Login successful") {
        //save data 
        navigate('/studentinterface');
      } else {
        setError("Invalid credentials");
      }
    } catch (err) {
      console.error('Error:', err); // Debugging: Log the error
      setError("An error occurred during login");
    }
  };

  return (
    <Container className='container'>
      <Paper className='paper'>
        <Typography variant="h5" className='header'>
          Log In
        </Typography>
        <form onSubmit={handleLogin}>
          <TextField
            label="Username"
            variant="outlined"
            fullWidth
            required
            className='textField'
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <TextField
            label="Password"
            type="password"
            variant="outlined"
            fullWidth
            required
            className='textField'
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          {error && <Typography color="error">{error}</Typography>}
          <Button
            type="submit"
            variant="contained"
            color="primary"
            className='button'
          >
            Login
          </Button>
          <Box className='link'>
            <Typography variant="body2">
              Don't have an account? <Link href="/studentreg">Sign Up Now</Link>
            </Typography>
          </Box>
        </form>
      </Paper>
    </Container>
  );
}

export default StudentLogin;
