import React, { useState } from 'react';
import { Container, Paper, Typography, TextField, Button, Link, Box } from '@mui/material';
import './student.css';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function StudentLogin() {
  const navigate = useNavigate();
  const [username,setName] = useState("");
  const [password, setPassword] = useState("");
  const[error,setError] = useState("");
  const handleLogin = async (e) => {
    e.preventDefault();
    // Add your authentication logic here if needed
    try{
      const response = await axios.post("http://localhost:8070/studnet/login",{
        name: username,
        password: password
      })
      if (response.data.status  === "Login successful"){
        navigate('/studentinterface');
      }else{
        setError("Invalid credentials")
      }
    } catch(err){
      console.error(err);
      setError("An error occurred during lohin");

    }
    
  };

  return (
    <Container className='container'>
      <Paper className='paper'>
        <Typography variant="h5" className='header'>
          Log In
        </Typography>
        <form onSubmit={handleLogin}>
          <TextField
            label="Username"
            variant="outlined"
            fullWidth
            required
            className='textField'
            value={username}
            onChange={(e) => setName(e.target.val)}
          />
          <TextField
            label="Password"
            type="password"
            variant="outlined"
            fullWidth
            required
            className='textField'
          />
          {error && <Typography color="error">{error}</Typography>}
          <Button
            type="submit"
            variant="contained"
            color="primary"
            className='button'
          >
            Login
          </Button>
          <Box className='link'>
            <Typography variant="body2">
              Don't have an account? <Link href="/studentreg">Sign Up Now</Link>
            </Typography>
          </Box>
        </form>
      </Paper>
    </Container>
  );
}

export default StudentLogin;
