import React from "react";
import { useNavigate } from "react-router-dom";
import { Container, Paper, Typography, TextField, Button, Link, Box } from '@mui/material';
import "./student.css";


function TeacherLogin() {
  const navigate = useNavigate();

  const handleLogin = (e) => {
    e.preventDefault(); // Prevent the default form submission

    // You can add your authentication logic here if needed

    // After successful login, navigate to the StudentDashboard page
    navigate('/teacherinterface');
  };

  return (
    <Container className='container'>
    <Paper className='paper'>
      <Typography variant="h5" className='header'>
        Log In
      </Typography>
      <form onSubmit={handleLogin}>
        <TextField
          label="Username"
          variant="outlined"
          fullWidth
          required
          className='textField'
          margin="normal"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <TextField
          label="Password"
          type="password"
          variant="outlined"
          fullWidth
          required
          className='textField'
          margin="normal"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        {error && <Typography color="error">{error}</Typography>}
        <Button
          type="submit"
          variant="contained"
          color="primary"
          className='button'
          margin="normal"
        >
          Login
        </Button>
        <Box className='link'>
          <Typography variant="body2">
            Don't have an account? <Link href="/studentreg">Sign Up Now</Link>
          </Typography>
        </Box>
      </form>
    </Paper>
  </Container>
  );

}

export default TeacherLogin;
