import React, { useEffect, useState } from 'react';
import { Button, TextField, Typography, Paper, Container } from '@mui/material';
import axios from 'axios';
// Import the CSS file

function StudentInterface() {
  const [studentName, setStudentName] = useState("");
  const [newpassword, setnewpassword] = useState("");
  const [newstudentage, setnewstudentage] = useState("");
  const [showUpdateForm, setShowUpdateForm] = useState(false);
  const [newUsername, setNewUsername] = useState("");
  
  const [message, setMessage] = useState("");

  useEffect(() => {
    // Retrieve the student's name from localStorage
    const name = localStorage.getItem('StudentName');
    if (name) {
      setStudentName(name);
    }
  }, []);

  const handleUpdateProfile = () => {
    setShowUpdateForm(true);
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();

    // Create an object to store the fields that should be updated
    const updatedFields = {};

    if (newUsername) updatedFields.newUsername = newUsername;
    if (newstudentage) updatedFields.newage = newstudentage;
    if (newpassword) updatedFields.newpassword = newpassword;

    if (Object.keys(updatedFields).length === 0) {
      setMessage("Please fill in at least one field to update.");
      return;
    }

    try {
        const response = await axios.put("http://localhost:8070/student/updateprofile", {
            name: studentName,
            ...updatedFields
        });

        if (response.data.status === "Update successful") {
            setMessage("Profile updated successfully");

            // Update localStorage or state with new username if it was changed
            if (newUsername) {
                setStudentName(newUsername);
                localStorage.setItem('StudentName', newUsername);
            }

            setShowUpdateForm(false); // Hide the form after successful update
        } else {
            setMessage("Failed to update profile");
        }
    } catch (err) {
        setMessage("An error occurred");
    }
};

  return (
    <Container className='student-interface'>
      <Typography variant="h4">
        Hi {studentName}
      </Typography>
      <Button
        variant="contained"
        color="primary"
        onClick={handleUpdateProfile}
      >
        Update Profile
      </Button>

      {showUpdateForm && (
        <Paper className='update-profile-paper'>
          <Typography variant="h6">
            Update Profile
          </Typography>
          <form onSubmit={handleFormSubmit}>
            <TextField
              label="New Username"
              variant="outlined"
              fullWidth
              margin="normal"
              value={newUsername}
              onChange={(e) => setNewUsername(e.target.value)}
            />
            <TextField
              label="New age"
              variant="outlined"
              fullWidth
              margin="normal"
              value={newstudentage}
              onChange={(e) =>  setnewstudentage(e.target.value)}
            />
            <TextField
              label="New Password"
              variant="outlined"
              fullWidth
              margin="normal"
              value={newpassword}
              onChange={(e) =>  setnewpassword(e.target.value)}
            />
           
            <Button
              type="submit"
              variant="contained"
              color="primary"
            >
              Update
            </Button>
            
          </form>
        </Paper>
      )}
      
      {message && (
        <Typography variant="h6" color="primary" style={{ marginTop: "20px" }}>
          {message}
        </Typography>
      )}
    </Container>
  );
}

export default StudentInterface;
