import React, { useEffect, useState } from 'react';
import { Button, TextField, Typography, Paper, Container, Box } from '@mui/material';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function StudentInterface() {
  const [studentName, setStudentName] = useState("");
  const [newpassword, setnewpassword] = useState("");
  const [newstudentage, forShowUpdateForm, setShowUpdateForm] = useState(false);
  const [newUsername, setNewUsername] = useState("");
  const [showDeleteForm, setShowDeleteForm] = useState(false);
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    // Retrieve the student's name from localStorage
    const name = localStorage.getItem('StudentName');
    if (name) {
      setStudentName(name);
    }
  }, []);

  const handleUpdateProfile = () => {
    setShowUpdateForm(true);
    setShowDeleteForm(false); // Ensure only one form is shown at a time
  };

  const handleDeleteAccountClick = () => {
    setShowDeleteForm(true);
    setShowUpdateForm(false); // Ensure only one form is shown at a time
  };

  const handleFormSubmit = async (e) => {
 e.preventDefault();

    // Create an object to store the fields that should be updated
    const updatedFields = {};

    if (newUsername) updatedFields.newUsername = newUsername;
    if (newstudentage) updatedFields.newage = newstudentage;
    if (newpassword) updatedFields.newpassword = newpassword;

    if (Object.keys(updatedFields).length === 0) {
      setMessage("Please fill in at least one field to update.");
      return;
    }

    try {
      const response = await axios.put("http://localhost:8070/student/updateprofile", {
        name: studentName,
        ...updatedFields
      });

      if (response.data.status === "Update successful") {
        setMessage("Profile updated successfully");

        // Update localStorage or state with new username if it was changed
        if (newUsername) {
          setStudentName(newUsername);
          localStorage.setItem('StudentName', newUsername);
        }

        setShowUpdateForm(false); // Hide the form after successful update
      } else {
        setMessage("Failed to update profile");
      }
    } catch (err) {
      setMessage("An error occurred");
    }
  };

  const handleDeleteAccountSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.delete('http://localhost:8070/student/delete', {
        data: {
          username: newUsername,
          password: newpassword,
        },
      });

      if (response.data.status === "Delete successful") {
        setMessage("Account deleted successfully");
        // Clear localStorage and redirect or take other necessary actions
        localStorage.removeItem('StudentName');
        navigate('/login'); // Redirect to login page after account deletion
      } else {
        setMessage("Failed to delete account");
      }
    } catch (err) {
      setMessage("An error occurred while deleting the account");
    }
  };

  return (
    <Container className='student-interface'>
      <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', mb: 2 }}>
        <Typography variant="h4">
          Hi {studentName}
        </Typography>
        <Box>
          <Button
            variant="contained"
            color="primary"
            onClick={handleUpdateProfile}
            sx={{ mr: 2 }}
          >
            Update Profile
          </Button>
          <Button
            variant="contained"
            color="secondary"
            onClick={handleDeleteAccountClick}
          >
            Delete Account
          </Button>
        </Box>
      </Box>

      {showUpdateForm && (
        <Paper className='update-profile-paper'>
          <Typography variant="h6">
            Update Profile
          </Typography>
          <form onSubmit={handleFormSubmit}>
            <TextField
              label="New Username"
              variant="outlined"
              fullWidth
              margin="normal"
              value={newUsername}
              onChange={(e) => setNewUsername(e.target.value)}
            />
            <TextField
              label="New Age"
              variant="outlined"
              fullWidth
              margin="normal"
              value={newstudentage}
              onChange={(e) => setnewstudentage(e.target.value)}
            />
            <TextField
              label="New Password"
              variant="outlined"
              fullWidth
              margin="normal"
              value={newpassword}
              onChange={(e) => setnewpassword(e.target.value)}
            />
            <Button
              type="submit"
              variant="contained"
              color="primary"
            >
              Update
            </Button>
          </form>
        </Paper>
      )}

      {showDeleteForm && (
        <Paper className='delete-account-paper'>
          <Typography variant="h6">
            Delete Account
          </Typography>
          <form onSubmit={handleDeleteAccountSubmit}>
            <TextField
              label="Username"
              variant="outlined"
              fullWidth
              margin="normal"
              value={newUsername}
              onChange={(e) => setNewUsername(e.target.value)}
            />
            <TextField
              label="Password"
              variant="outlined"
              fullWidth
              margin="normal"
              type="password"
              value={newpassword}
              onChange={(e) => setnewpassword(e.target.value)}
            />
            <Button
              type="submit"
              variant="contained"
              color="secondary"
            >
              Delete
            </Button>
          </form>
        </Paper>
      )}

      {message && (
        <Typography variant="h6" color="primary" style={{ marginTop: "20px" }}>
          {message}
        </Typography>
      )}
    </Container>
  );
}

export default StudentInterface;
